var modalData={norrmade:{title:'Norrmade Digital Showroom',detail:'I worked with a furniture company called Normmade to idea a digital showroom for their main website. The solution is an interactive image that allow the user to see the furniture in a real life context and get more details about it by clicking on the furniture on the picture.',link:'https://benji94p.github.io/digitalshowroomTEST/digitalshowroom.html',media:["media/norrmade1.png","media/norrmade2.png","media/norrmade3.png"]},translator:{title:'Translator Web Page',detail:'I designed and coded a website in HTML,CSS & JS, for my own translation freelancing business while I am travelling abroad.',link:"https://digitartpzm.dk/translator_webpage/root.html",media:["media/translator1.png","media/translator2.png","media/translator3.png"]},game:{title:'Western Family Game',detail:'I coded a whole game that you can play in the browser for a school project. It is coded in HTML,CSS and some advanced programming in Javascript.',link:'http://www.powur.com/with/42',media:["media/game1.png","media/game2.png","media/game3.png"]},uprooted:{title:'Uprooted Video',detail:'This project showcase my video skills. A short montage on our experience on being expats. The video was edited in Premiere Pro.',link:'https://www.youtube.com/watch?v=-2ki4vJhVHo',media:["media/uprooted1.png","media/uprooted2.png","media/uprooted3.png"]},};$("#uprooted, #game, #translator, #norrmade").click(function(iconObject){$('.modal').modal('toggle');let iconId=iconObject.target.id;document.querySelector(".modal-title").textContent=modalData[iconId].title;document.querySelector(".modal-description").textContent=modalData[iconId].detail;document.querySelector("#point-wb").href=modalData[iconId].link;document.querySelector("#first-carousel").src=modalData[iconId].media[0];document.querySelector("#second-carousel").src=modalData[iconId].media[1];document.querySelector("#third-carousel").src=modalData[iconId].media[2]})
